import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import api from '../../../services/api';
import CreateSong from '../CreateSong';




// import { Container } from './styles';

export default function DetailsAlbum(props) {

  const [albums, setAlbums] = useState([]);



  useEffect(() => {
    async function AlbumDetails(){
      const { id } = props.match.params;
      const response = await api.get(`/albums/${id}`);
      const data  = response.data;
      setAlbums(data);
      console.log( data);
      console.log(id);

    }  

    AlbumDetails();
  },[props.match.params])

  async function handleDeleteAlbum(id){
    try{
      if(!window.confirm("Deseja mesmo excluir o album?")) return;
      await api.delete(`songs/${id}`);
      setAlbums(albums.filter(album => album.id !== id));

    }catch(err){
      alert('erro ao deletar. Tente novamente');
    }
  }

  return (

    <div className="box">
      <h1 className="title">
        Detalhes do Album {" "}
        <small>
          <Link to="/albums">
            Voltar
          </Link>
        </small>
      </h1>
      <div className="columns">
        <div className="column">
          <img src="" className="image" alt="" />
          <br />
          <div className="field is-grouped">
            <div className="file control">
              <label className="file-label">
                <input type="file" className="file-input"name="image"/>
                <span className="file-cta">
                  <span className="file-icon">
                    <i className="fas fa-upload" />
                  </span>
                  <div className="file-label">
                    Escolher imagem
                  </div>
                </span>
              </label>
            </div>
            <button className="button is-info-control">Atualizar</button>
          </div>
        </div>
        <div className="column">
          <CreateSong />
          {albums.map(album => 
            album.song.map((songs, idx) => 
            (<p key={songs.id}>{idx + 1} - {songs.name} 
            <span className="icon has-text-danger" 
            onClick={() => handleDeleteSong(songs.id)}>
              <i className="fas fa-trash-alt" />
              </span>
            </p>)
          ))}
        </div>
      </div>
    </div>
  );
}
