import React from 'react';


// import { Container } from './styles';

export default function Albums({albums}) {
  function handleDeleteAlbum(){

    console.log("erro ao excluir, tente novamente");
}
  return (
    <div className="column">
        {albums.map(album => (
                <div key={album.id}className="media box">
                <div className="media-left"><span className="tag is-info">{album.id}</span></div>
                <div className="media-content">
                  <p className="title is-4">{album.name}</p>
                  <p>Artist: {album.artist}</p>
                  </div>
                <div className="media-right">
                  <button className="button is-success"><i className="fas fa-info-circle" /></button>
                  <button className="button is-danger"><i className="fas fa-trash-alt" onClick={handleDeleteAlbum} /></button>
                </div>
              </div>
              ))}

    </div>
  );
}
