import axios from 'axios';
import React, { useEffect, useState }from 'react';
import './App.css';

function App() {
const [albums, setAlbums] = useState([]);

useEffect(async ()=>{
 const response = await axios.get("http://localhost:3333/albums");
 const data = await response.json();

 setAlbums(data);

}, [])
  return (
    <>
      <ul>
        {albums.map(album => (
          <li key={album.id}>
            {album.id} - {album.name}
          </li>
        ))}
      </ul>
    </>
  );
}

export default App;
