import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import api from '../../../services/api';



// import { Container } from './styles';

export default function DetailsAlbum(props) {

  const [album, setAlbum] = useState([]);

  useEffect(() => {
    async function AlbumDetails(){
      const album_id = props.match.params.id;
      const response = await api.get(`/albums/${album_id}`);
      setAlbum(response.data);
      console.log(album_id);
      console.log(response.data);
    }  
    AlbumDetails();
  },[])



  return (
    <div className="box">
      <h1 className="title">
        Detalhes do Album {" "}
        <small>
          <Link to="/albums">
            Voltar
          </Link>
        </small>
      </h1>
      <div className="columns">
        <div className="column">
          <img src="" className="image" alt="" />
          <br />
          <div className="field is-grouped">
            <div className="file control">
              <label className="file-label">
                <input type="file" className="file-input"name="image"/>
                <span className="file-cta">
                  <span className="file-icon">
                    <i className="fas fa-upload" />
                  </span>
                  <div className="file-label">
                    Escolher imagem
                  </div>
                </span>
              </label>
            </div>
            <button className="button is-info-control">Atualizar</button>
          </div>
        </div>
        <div className="column">
          {album.song ? album.song.map(songs => <p>{songs.name}</p>) : null}
            <p>{album.name}</p>)}
        </div>
      </div>
    </div>
  );
}
