import React, {useState, useEffect} from 'react';

// import { Container } from './styles';

export default function CreateSong({albums ,handleNewSong}) {
    const {album, SetAlbuns} = useState('');
    const {artist, SetArtits} = useState('');

    useEffect(() => {
      SetAlbuns("");
      SetArtits("");

    }, [albums])

  return (
    <div className="field has-addons">
        <div className="control is-expanded">
            <input type="text" className="input" placeholder="digite o nome da musica"/>
        </div>
        <div className="control">
            <button className=" button is-success" onClick={handleNewSong}>
                <i className="fas fa-plus"/>
            </button>
        </div>
    </div>
  );
}
